import { Component } from '@angular/core';



/**
 * Componente principal de la aplicación.
 *
 * Este componente representa el contenedor principal de la aplicación HelpCook.
 * Proporciona el menú principal de navegación y permite expandir/cerrar el menú lateral.
 *
 */
@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
   /**
   * Título de la aplicación.
   */
  title = 'HelpCook';

    /**
   * Indica si el menú lateral está abierto o cerrado.
   */
  opened = false;

   /**
   * Indica si el menú lateral está expandido o no.
   */
  expanded = false;

    /**
   * Crea una instancia del componente AppComponent.
   */
  constructor(){}

  /**
   * Alterna la visibilidad del menú lateral.
   */
  toggleSidenav() {
    this.expanded = !this.expanded;
  }
}
<!DOCTYPE html>
<html lang="en">
<head>
  <meta charset="UTF-8">
  <meta http-equiv="X-UA-Compatible" content="IE=edge">
  <meta name="viewport" content="width=device-width, initial-scale=1.0">
  <title>App Component</title>
  <link rel="stylesheet" href="app.component.css" type="text/css">
  <link rel="stylesheet" href="https://cdn.jsdelivr.net/npm/bootstrap-icons@1.10.3/font/bootstrap-icons.css"> <!--ICONOS-->
</head>
<body>
  <div class="container-fluid">
    <app-navbar></app-navbar>

    <router-outlet></router-outlet>

    <app-footer></app-footer>
  </div>
</body>
</html>
